import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (!mobile) setIsOpen(true);
      else setIsOpen(false);
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial check

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <>
      {/* Mobile Toggle Button */}
      <button 
        className="lg:hidden fixed top-4 left-4 z-50 bg-primary text-text-primary p-2 rounded-md shadow-md border border-secondary"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle Menu"
      >
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          className="h-6 w-6" 
          fill="none" 
          viewBox="0 0 24 24" 
          stroke="currentColor"
        >
          {isOpen ? (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          ) : (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          )}
        </svg>
      </button>

      {/* Sidebar */}
      <div 
        className={`bg-primary text-text-primary h-screen fixed left-0 shadow-lg transition-all duration-300 ease-in-out z-40 overflow-hidden border-r border-secondary ${
          isOpen ? 'w-64 translate-x-0' : 'w-0 -translate-x-full lg:w-64 lg:translate-x-0'
        }`}
      >
        <div className="p-5 flex justify-center">
          <img 
            src="/bits%20logo.png" 
            alt="BITS Pilani Logo" 
            className="w-32 h-auto"
          />
        </div>
        <nav className="mt-8">
          <ul className="space-y-2 px-4 whitespace-nowrap">
            <li>
              <NavLink 
                to="/" 
                className={({ isActive }) => 
                  `block py-2 px-4 rounded transition duration-200 ${
                    isActive ? 'bg-secondary text-text-primary' : 'hover:bg-surface hover:text-text-primary'
                  }`
                }
                onClick={() => isMobile && setIsOpen(false)}
              >
                Home
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="/registration" 
                className={({ isActive }) => 
                  `block py-2 px-4 rounded transition duration-200 ${
                    isActive ? 'bg-secondary text-text-primary' : 'hover:bg-surface hover:text-text-primary'
                  }`
                }
                onClick={() => isMobile && setIsOpen(false)}
              >
                Registration
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="/fixtures" 
                className={({ isActive }) => 
                  `block py-2 px-4 rounded transition duration-200 ${
                    isActive ? 'bg-secondary text-text-primary' : 'hover:bg-surface hover:text-text-primary'
                  }`
                }
                onClick={() => isMobile && setIsOpen(false)}
              >
                Fixtures
              </NavLink>
            </li>
          </ul>
        </nav>
      </div>

      {/* Overlay when sidebar is open on mobile */}
      {isOpen && isMobile && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-75 z-30 lg:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}
    </>
  );
};

export default Sidebar;
